        @media (max-width:700px) {
            transform: scale(1.2);
            border: none;
        }
      }
      

      
      /* Fading animation */
      .fade {
        -webkit-animation-name: fade;
        -webkit-animation-duration: 1.5s;
        animation-name: fade;
        animation-duration: 1.5s;
      }
      
      @-webkit-keyframes fade {
        from {opacity: .2}
        to {opacity: 1}
      }
      
      @keyframes fade {
        from {opacity: .2}
        to {opacity: 1}
      }



      
