@import url(https://cdn.rawgit.com/tonsky/FiraCode/1.205/distr/fira_code.css);


html {
    box-sizing: border-box;
  }
a {
    color: inherit;
    text-decoration: none

}
*, *::before, *::after {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;

    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

body {
    display: flex;
    flex-direction: column;
    width: 100%;
    // font-family: 'Fira Code', sans-serif;
    font-family: 'Quicksand', sans-serif;
    background-image: linear-gradient( orangered, orange, orangered );
    ;
}
.nav {
    left: -1px;
    margin: auto;
    display: flex;
    position: fixed;
    justify-content: space-between;
    width: 100%;
    z-index: 23;
    opacity: 0.9;
    margin-bottom: 100px;
    border: 3px solid orangered;
    color: rgb(255, 60, 0);
    background-color: rgb(0, 0, 0);
     .social {
         display: flex;
         align-items: center;
         justify-content:flex-end;
         justify-self: flex-end;
         margin-right: 20px;
         transform: scale(1);
         gap: 20px;
         i {
             transform: scale(1);
             cursor: pointer;
             transition: transform 0.2s ease-in, color 0.3s ease-in;
         }
         i:hover {
             transform: scale(1.2);
             color: rgba(255, 0, 0, 0.918);
         }
     }
    // border-radius: 0 20px 0 20px;
    .navbar {
        display: flex;
        justify-content: space-evenly;
        width: 100%;
        padding: 5px;
        &__item {
            margin: 0;
            list-style-type: none
        }
    }
}

.main {


    .hero {
        font-weight: 900;
        margin: 150px 100px;
        text-align: center;
        // border-bottom: 2px solid black;
        padding:100px 100px 0 100px;
        font-size: 2.3rem;
        @media (max-width:1000px){
            margin: 150px auto;
            font-size: 1rem;
            padding: 0;
        }
        

        // background-image: linear-gradient(rgb(255, 124, 2),orange,  rgb(255, 255, 255));
    }
    .slide-in {
        opacity: 0;
        transition: all .5s;
      }
      .align-left.slide-in {
        transform: translateX(-30%) scale(0.95);
      }
      
      .align-right.slide-in {
        transform: translateX(30%) scale(0.95);
      }
      .slide-in.active {
        opacity: 1;
        transform: translateX(0%) scale(1);
      }
    &__row {
        background-color: rgb(0, 0, 0);
        color: orangered;
        display: flex;
        flex-wrap: wrap;
        margin: 250px 0;
        box-shadow: 0 4px 16px -2px gray;
        // mozna dodac padding dla duzych monitorow
        
            @media (max-width:1000px){
                flex-direction: column;
                align-items: center;
                margin: 50px auto;
                padding: 0;
            }
    
        img {
            width: 40%;
            min-width: 200px;
        }
        &__content {
            
            display: flex;
            flex-direction: column;
            text-align: left;
            margin: auto;
            padding: 20px 100px;
            width: 60%;
            font-size: 1.8rem;
            @media (max-width:1000px){
                margin: auto;
                padding: 0;
                font-size: 1rem;
                text-align: center;

            }
        }
        &__i {
            margin: auto;
            i {
                color: orangered;
                text-shadow: 9px 9px 1px rgba(255, 0, 0, 0.397);
    
                    padding: 60px;
                    opacity: 0.9;
                    @media (max-width:1000px){
                        margin: auto;
                        padding: 5px;
        
                    }
        
                }
        }
    }
}






