    .slideshow-container {
        display: flex;
        max-width: 900px;
        position: relative;
        margin: auto; 
        a {
            color: orangered;
            
        }
        img {
            display: flex;
            width: 80%;
            height: auto;
            margin: auto;
        }
      }

      
      /* Hide the images by default */
      .mySlides {
        display: none;
      }
      
      /* Next & previous buttons */
      .prev, .next {
        cursor: pointer;
        position: absolute;
        top: 50%;
        width: auto;
        margin-top: -22px;
        padding: 16px;
        font-weight: bold;
        font-size: 18px;
        transition:border 0.6s ease-in;
        border-radius: 13px 0 0 13px;
        user-select: none;


    }
    
    /* Position the "next button" to the right */
    .next {
        border-radius: 0 13px 13px 0;
        right: 0;
      }
      
      /* On hover, add a black background color with a little bit see-through */
      .prev:hover, .next:hover {
        border: 1px solid orangered;

      }
      

      
      /* Fading animation */
      .fade {
        -webkit-animation-name: fade;
        -webkit-animation-duration: 1.5s;
        animation-name: fade;
        animation-duration: 1.5s;
      }
      
      @-webkit-keyframes fade {
        from {opacity: .2}
        to {opacity: 1}
      }
      
      @keyframes fade {
        from {opacity: .2}
        to {opacity: 1}
      }



      
